mapper.insert:

insert into op  (op_name, op_explain, op_order, product_no)
values (#{op_name}, #{op_explain}, #{op_order}, #{product_no});




select last_insert_id()

mapper.update:

	update product_image
		set url = #{url} , 
			is_main = #{is_main} 
    	where no = #{no};

mapper.select:

select 
	no
from op where product_no = #{product_no}
